import React, { Component } from "react";

class Like extends Component {
	render() {
		const { liked } = this.props;
		return <i className={this.handleLike(liked)} aria-hidden='true' />;
	}

	handleLike = (x) => {
		let classes = "fa fa-heart";
		if (!x) return (classes += "-o");
		else return classes;
	};
}

export default Like;
