import React, { Component } from "react";
import { getMovies } from "../services/fakeMovieService.js";
import Heart from "./symbol/Heart.jsx";

class Movies extends Component {
	state = {
		movies: getMovies(),
		count: 0
	};
	handleDelete = (movie) => {
		const moviesAfterDeletion = this.state.movies.filter(
			(m) => m._id !== movie._id
		);
		this.setState({ movies: moviesAfterDeletion });
	};
	handleReset = () => {
		console.log("restore is clicked!!");
		this.setState({ movies: getMovies() });
	};
	handleLike = () => {
		this.state.count++;
		if (this.state.count === 0) {
			return true;
		} else return false;
	};
	render() {
		const { length: size } = this.state.movies;

		if (size === 0) {
			return (
				<div>
					<p>There are no movies left in the DB!</p>
					<button
						onClick={() => this.handleReset()}
						className='btn-group-lg btn-sm'>
						click here to restore DB!
					</button>
				</div>
			);
		}
		return (
			<React.Fragment>
				<p>Showing {size} movies in the DB</p>
				<table className='table'>
					<thead>
						<tr>
							<th>Title</th>
							<th>Genre</th>
							<th>Stock</th>
							<th>Rate</th>
							<th></th>
						</tr>
					</thead>
					<tbody>
						{this.state.movies.map((movie) => (
							<tr key={movie._id}>
								<td>{movie.title}</td>
								<td>{movie.genre.name}</td>
								<td>{movie.numberInStock}</td>
								<td>{movie.dailyRentalRate}</td>
								<td>
									<Heart
										onClick={() => this.state.handleLike()}
										buttonState={!this.state.handleLike}
									/>
									<button
										onClick={() => this.handleDelete(movie)}
										className='btn btn-danger btn-sm'>
										Delete
									</button>
								</td>
							</tr>
						))}
					</tbody>
				</table>
			</React.Fragment>
		);
	}
}

export default Movies;
