import React, { Component } from "react";
import { getMovies } from "../services/fakeMovieService.js";

class Movies extends Component {
	state = {
		movies: getMovies()
	};

	handleDeletion = (movie) => {
		const movieAfterDeletion = this.state.movies.filter(
			(m) => m._id !== movie._id
		);
		this.setState({ movies: movieAfterDeletion });
	};

	render() {
		const { length: size } = this.state.movies;
		if (size === 0) {
			return <p>No more movies found in the database!</p>;
		}
		return (
			<React.Fragment>
				<span>{size} movies is/are found in the database</span>
				<table className='table'>
					<thead>
						<tr>
							<th>Title</th>
							<th>Genre</th>
							<th>Stock</th>
							<th>Rate</th>
							<th></th>
						</tr>
					</thead>
					<tbody>
						{this.state.movies.map((movie) => {
							<tr>
								<td>{movie.title}</td>
								<td>{movie.genre.name}</td>
								<td>{movie.numberInStock}</td>
								<td>{movie.dailyRentalRate}</td>
								<td>
									<button
										onClick={() => this.handleDeletion(movie)}
										className='btn btn-danger btn-sm'>
										Delete
									</button>
								</td>
							</tr>;
						})}
					</tbody>
				</table>
			</React.Fragment>
		);
	}
}

export default Movies;
