import React, { Component } from "react";
import { getMovies } from "../services/fakeMovieService.js";

const TableRow = ({ data }) => (
	<tr>
		<td key={data.title}>{data.title}</td>
		<td key={data.Genre}>{data.Genre}</td>
		<td key={data.Stock}>{data.Stock}</td>
		<td key={data.Rate}>{data.Rate}</td>
	</tr>
);

class Movies extends Component {
	state = {
		movies: getMovies()
	};

	render() {
		let data = this.state.movies;
		return (
			<main className='container'>
				<span></span>
				<table className='table m-2' data={data}>
					<thead>
						<tr>
							<th scope='col'>Title</th>
							<th scope='col'>Genre</th>
							<th scope='col'>Stock</th>
							<th scope='col'>Rate</th>
						</tr>
					</thead>
					<tbody>
						{data.map((e) => (
							<TableRow
								data={e.title}
								data={e.genre.name}
								data={e.numberInStock}
								data={e.dailyRentalRate}></TableRow>
						))}
						{/* {data.map((e) => (
							<TableRow data={e.genre}></TableRow>
						))}
						{data.map((e) => (
							<TableRow data={e.numberInStock}></TableRow>
						))}
						{data.map((e) => (
							<TableRow data={e.dailyRentalRate}></TableRow>
						))} */}
					</tbody>
				</table>
			</main>
		);
	}
}

export default Movies;
