import React, { Component } from "react";
import { getMovies } from "../services/fakeMovieService.js";

class Movies extends Component {
	state = {
		movies: getMovies()
	};

	render() {
		const TableRow = ({ row }) => (
			<tr>
				<td key={row.title}>{row.title}</td>
				<td key={row.Genre}>{row.Genre}</td>
				<td key={row.Stock}>{row.Stock}</td>
				<td key={row.Rate}>{row.Rate}</td>
			</tr>
		);
		const Table = ({ data }) => (
			<table>
				{data.map((row) => {
					<TableRow row={row} />;
				})}
			</table>
		);
		let data = this.state.movies;
		return (
			<main className='container'>
				<div>
					<span></span>
					<table className='table m-2' data={data}>
						<tr>
							<th scope='col'>Title</th>
							<th scope='col'>Genre</th>
							<th scope='col'>Stock</th>
							<th scope='col'>Rate</th>
						</tr>
					</table>
					<tbody>
						<Table data={data}></Table>
					</tbody>
				</div>
			</main>
		);
	}
}

export default Movies;
