import React, { Component } from "react";
import { getMovies } from "../services/fakeMovieService.js";
import { getMovieById } from "../services/fakeMovieService.js";

class Movies extends Component {
	state = {
		movies: getMovies()
	};

	render() {
		return (
			<main className='container'>
				<div>
					<span></span>
					<table class='table'>
						<thead class='thead-dark'>
							<tr>
								<th scope='col'>Title</th>
								<th scope='col'>Genre</th>
								<th scope='col'>Stock</th>
								<th scope='col'>Rate</th>
							</tr>
						</thead>
						<tbody>
							{myList.map((item, i) => {
								return [
									<tr key={i} onClick={toggleMobileOpen.bind(this, i)}>
										<td className='toggler'>
											{item.mobile_open && <ArrowUp />}
											{!item.mobile_open && <ArrowDown />}
										</td>
										<td>{item.elem_one}</td>
										<td>{item.elem_two}</td>
										<td>{item.elem_three}</td>
									</tr>,

									//This inline conditional makes it weird but this works
									...[
										item.mobile_open && (
											<tr className='test-td'>
												<td>...</td>
											</tr>
										)
									]
								];
							})}
						</tbody>
					</table>
				</div>
			</main>
		);
	}
}

export default Movies;
