import React, { Component } from "react";

class Like extends Component {
	render() {
		const { liked, onClick } = this.props;
		return (
			<i
				onClick={onClick}
				style={{ cursor: "pointer" }}
				className={this.handleLike(true)}
				aria-hidden='true'
			/>
		);
	}

	handleLike = (x) => {
		if (!x) return "fa fa-heart-o";
		else return "fa fa-heart";
	};
}

export default Like;
